#include <vector>
#include <iostream>
#include <cmath>


using namespace std;

double helper(const vector<int>& min_nums, const vector<int>& max_nums, const size_t size_min, const size_t size_max) {
    size_t half = (size_min + size_max) / 2;
    size_t k1 = 0;
    auto n = max(size_min, size_max);
    for (size_t b = n / 2; b >= 1; b /= 2) {
        while (b+k1 < n && max_nums[b+k1] <= min_nums[size_min -1]) k1+=b;
    }
    cout << "K-max: " << max_nums[k1] << endl;

    size_t k2 = 0;
    for (size_t b = n/2; b>= 1; b /=2) {
        while (b+k2 < n && min_nums[b+k2] <= max_nums[0]) k2+=b;
    }
    cout << "K-min " << min_nums[k2] << endl;

    cout << "DIFF: " << max(k2,k1)-min(k1,k2) << " HALF: " <<  half << endl;

    return 1.0;
}

double compute_med(const vector<int>& nums, const size_t& size) {
    if (size % 2 == 1)
        return nums[size / 2];
    return (nums[size / 2 - 1] + nums[size / 2])/2.0;
}

double smaller(const vector<int>& smaller, const vector<int>& bigger, size_t size_smaller, const size_t& size_bigger) {

    const auto total {size_smaller + size_bigger};
    const auto ind {size_bigger-static_cast<int>(ceil(static_cast<double>(total) / 2.0)) };
    if (total % 2 == 1) {
        if (bigger[ind] < 0)
            return bigger[size_bigger - ind - 1];
        return bigger[ind];
    }

    if (ind == 0) {
        return (smaller[size_smaller - 1] + bigger[0]) / 2.0;
    }
    if (bigger[ind] < 0)
        return (bigger[ind] + bigger[ind+1]) / 2.0;
    return (bigger[ind] + bigger[ind-1]) / 2.0;

}

double findMedianSortedArrays(const vector<int>& nums1, const vector<int>& nums2) {
    const auto size_1 {nums1.size()};
    const auto size_2 {nums2.size()};
    if (size_1 == 0 && size_2 == 0)
        return 0.0;
    if (size_1 == 1 && size_2 == 0)
        return nums1[0];
    if (size_1 == 0 && size_2 == 1)
        return nums2[0];
    if (size_1 == 0)
        return compute_med(nums2, size_2);
    if (size_2 == 0)
        return compute_med(nums1, size_1);

    if (size_1 == 1 && size_2 == 1 )
        return static_cast<double>(nums1[0] + nums2[0]) / 2.0;

    if (nums1[size_1 - 1] <= nums2[0] || nums2[size_2 - 1] <= nums1[0]) {
        if (size_1 < size_2)
            return smaller(nums1, nums2, size_1, size_2);
        return smaller(nums2, nums1, size_2, size_1);
    }

    if (nums1[size_1 - 1] < nums2[size_2 - 1]) {

        return helper(nums1, nums2, size_1, size_2);
    }

    return helper(nums2, nums1, size_2, size_1);

}

